import React, { useEffect, useState } from 'react';

const TempApp = () =>{

    const[city, setCity] = useState(null);
    const[search, setSearch] = useState("Mumbai");

    useEffect(()=>{
        const getWeatherData = async() =>{
            const response = await fetch(`api.openweathermap.org/data/2.5/weather?q=${search}&appid=efe98dacfde9f1370cc56b881dd1377d`);
        }

        getWeatherData();
    }, [search]);

  return(
    <>
        <h1 className="heading">Weather Live App</h1>
      <div className="main_box">
            <input type="search" name="search" placeholder="Search by city" onChange={(event)=>{
                setCity(event.target.value);
            }}
                value={city}
            />
            <div className="div_city">
                <h1>
                <i class="fas fa-street-view"></i> Pune
                </h1>

            <h1> 5.25°Cel </h1>
            <h3>Min: 5.25°Cel | Max: 5.75°Cel</h3>
            </div>
      </div>
    </>
  );
}

export default TempApp;
